import { makeBadge } from 'badge-maker'
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const dataPath = path.join(__dirname, '../assets/badge_data.json');
const outputDir = path.join(__dirname, '../assets/badges');

if (!fs.existsSync(dataPath)) {
  console.error('Badge data file not found:', dataPath);
  process.exit(1);
}

if (!fs.existsSync(outputDir)) {
  fs.mkdirSync(outputDir, { recursive: true });
}

const { total_clones, total_downloads } = JSON.parse(fs.readFileSync(dataPath, "utf8"));

const createBadge = (label, message, color, filename) => {
  const format = {
    label,
    message: message.toLocaleString(),
    color,
    style: "for-the-badge",
    logoBase64: "data:image/svg+xml;base64,",
  };

  const svg = makeBadge(format);
  const outputPath = path.join(outputDir, filename);
  fs.writeFileSync(outputPath, svg);
  console.log(`Badge saved to ${outputPath}`);
};

createBadge("Downloads Since 2/4/2025", total_downloads, "#1793d1", "downloads-badge.svg");
createBadge("Clones Since 2/4/2025", total_clones, '#2ea44f', "clones-badge.svg");
